$("document").ready(function(){
  $(".shownavbar").click(function(){
    $("#div1").fadeIn();
    $("#div2").fadeIn();
    $("#div3").fadeIn();
    $("#div4").fadeIn()
  });
  $(".hide").click(function(){
    $("#div1").fadeOut();
    $("#div2").fadeOut();
    $("#div3").fadeOut();
    $("#div4").fadeOut();
  });
});

